INSERT INTO `mm_paminnelse_sql` VALUES (1, 'När jag får nya meddelanden', 1, 'SELECT\n   mottagare.epost as epost,\n   mottagare.fNamn as Namn,\n   group_concat(DISTINCT avsandare.aNamn SEPARATOR \\''\\\\n\\'') as avsandarLista\nFROM\n   mm_medlem mottagare,\n   mm_medlem avsandare,\n   mm_motiomeramail mail,\n   mm_paminnelse_aktiva reminder\nWHERE\n   mottagare.id in (#medlemslista#) AND\n   mail.from_id = avsandare.id AND\n   mail.to_id = mottagare.id AND\n   mail.is_read = 0 AND\n   reminder.medlem_id = mottagare.id AND\n   reminder.senaste_utskick &lt; mail.date_sent AND\n   reminder.sql_id = #sql_id#\nGROUP BY\n   mottagare.id', 'Nya MotiomeraMail', 'Hej #Namn#!    \n\nFöljande medlemmar på Motiomera har skickat mail till dig:\n#avsandarLista#    \n\nLogga in på www.motiomera.se för att läsa dina mail.    \n\nMed vänliga hälsningar,\nMotiomera.se', 1);
INSERT INTO `mm_paminnelse_sql` VALUES (2, 'När någon lägger till mig som vän', 1, 'SELECT\n   medlem.epost as epost,\n   medlem.fNamn as Namn,\n   group_concat(DISTINCT kontakt.aNamn SEPARATOR \\''\\\\n\\'') as avsandarLista\nFROM\n   mm_medlem medlem,\n   mm_medlem kontakt,\n   mm_kontakt vanner,\n   mm_paminnelse_aktiva reminder\nWHERE\n   medlem.id in (#medlemslista#) AND\n   vanner.kontakt_id = kontakt.id AND\n   vanner.medlem_id = medlem.id AND\n   reminder.medlem_id = medlem.id AND\n   reminder.senaste_utskick &lt; vanner.datum AND\n   reminder.sql_id = #sql_id#\nGROUP BY\n   medlem.id', 'Nya vänner på Motiomera', 'Hej #Namn#!  \n\nFöljande användare har lagt till dig som vän på motiomera:\n#avsandarLista#  \n\nMed vänliga hälsningar,\nMotiomera.se', 1);
